import React, { useState } from 'react';
import { Smile, ArrowRight, User, Heart, Music, Book, Gamepad } from 'lucide-react';
import './BuddyOnboarding.css'; // We'll create this CSS file

const BuddyOnboarding = ({ onSubmit, isDarkMode }) => {
  const [userProfile, setUserProfile] = useState({
    name: '',
    hobbies: '',
    favorites: ''
  });
  const [currentStep, setCurrentStep] = useState(1);

  const colors = {
    primary: '#6366f1',
    secondary: '#8b5cf6',
    accent: '#ec4899',
    darkBg: '#0f172a',
    lightBg: '#f8fafc',
    darkCard: '#1e293b',
    lightCard: '#ffffff',
    success: '#10b981',
    warning: '#f59e0b',
    error: '#ef4444',
  };

  const handleNext = () => {
    if (currentStep < 3) {
      setCurrentStep(currentStep + 1);
    } else {
      onSubmit(userProfile);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserProfile(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className={`onboarding-container ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="onboarding-card">
        <div className="onboarding-header">
          <div className="onboarding-logo">
            <Smile size={48} />
          </div>
          <h1 className="onboarding-title">Welcome to Buddy!</h1>
          <p className="onboarding-subtitle">
            {currentStep === 1 && "Let's start by getting to know each other!"}
            {currentStep === 2 && "Tell me about your interests and hobbies!"}
            {currentStep === 3 && "Almost done! Share some of your favorites with me."}
          </p>

          <div className="step-indicator">
            {[1, 2, 3].map((step) => (
              <React.Fragment key={step}>
                <div className={`step ${step <= currentStep ? 'active' : ''} ${step === currentStep ? 'current' : ''}`}>
                  {step}
                </div>
                {step < 3 && (
                  <div className={`step-line ${step < currentStep ? 'active' : ''}`}></div>
                )}
              </React.Fragment>
            ))}
          </div>
        </div>

        <div className="form-container">
          {/* Step 1: Name */}
          {currentStep === 1 && (
            <div className="form-group">
              <label className="form-label">
                What should I call you? *
              </label>
              <input
                type="text"
                name="name"
                value={userProfile.name}
                onChange={handleInputChange}
                className="form-input"
                placeholder="Your name or nickname"
                autoFocus
              />
              <div className="icon-container">
                <div className="icon-circle">
                  <User size={24} />
                </div>
              </div>
            </div>
          )}

          {/* Step 2: Hobbies */}
          {currentStep === 2 && (
            <div className="form-group">
              <label className="form-label">
                What are you into?
              </label>
              <input
                type="text"
                name="hobbies"
                value={userProfile.hobbies}
                onChange={handleInputChange}
                className="form-input"
                placeholder="Gaming, music, sports, cooking, reading..."
                autoFocus
              />
              <div className="icon-container">
                <div className="icon-circle">
                  <Music size={24} />
                </div>
                <div className="icon-circle">
                  <Book size={24} />
                </div>
                <div className="icon-circle">
                  <Gamepad size={24} />
                </div>
              </div>
            </div>
          )}

          {/* Step 3: Favorites */}
          {currentStep === 3 && (
            <div className="form-group">
              <label className="form-label">
                What are some of your favorites?
              </label>
              <input
                type="text"
                name="favorites"
                value={userProfile.favorites}
                onChange={handleInputChange}
                className="form-input"
                placeholder="Movies, music, books, foods, etc."
                autoFocus
              />
              <div className="icon-container">
                <div className="icon-circle">
                  <Heart size={24} />
                </div>
              </div>
            </div>
          )}

          <button
            onClick={handleNext}
            disabled={currentStep === 1 && !userProfile.name.trim()}
            className="onboarding-button"
          >
            {currentStep < 3 ? (
              <>
                Continue
                <ArrowRight size={18} />
              </>
            ) : (
              <>
                <Heart size={18} />
                Let's be friends!
              </>
            )}
          </button>
        </div>

        <div className="onboarding-footer">
          {currentStep === 1 && "We'll keep your information private and secure"}
          {currentStep === 2 && "This helps me understand you better!"}
          {currentStep === 3 && "Ready to start our friendship!"}
        </div>
      </div>
    </div>
  );
};

export default BuddyOnboarding;
