import React, { useState } from 'react';
import { Smile, ArrowRight, User, Heart, Music, Book, Gamepad } from 'lucide-react';

const BuddyOnboarding = ({ onSubmit, isDarkMode }) => {
  const [userProfile, setUserProfile] = useState({
    name: '',
    hobbies: '',
    favorites: ''
  });
  const [currentStep, setCurrentStep] = useState(1);

  const styles = {
    container: {
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      background: isDarkMode
        ? 'linear-gradient(135deg, #0f172a 0%, #1e293b 100%)'
        : 'linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)',
      color: isDarkMode ? '#f8fafc' : '#1e293b',
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
      padding: '20px',
      position: 'relative',
      overflow: 'hidden'
    },
    card: {
      maxWidth: '500px',
      width: '100%',
      margin: '0 auto',
      background: isDarkMode ? 'rgba(15, 23, 42, 0.95)' : 'rgba(255, 255, 255, 0.95)',
      borderRadius: '24px',
      boxShadow: isDarkMode
        ? '0 10px 40px rgba(0, 0, 0, 0.4)'
        : '0 10px 40px rgba(0, 0, 0, 0.1)',
      backdropFilter: 'blur(12px)',
      padding: '40px',
      position: 'relative',
      zIndex: 2,
      marginTop: '40px'
    },
    header: {
      textAlign: 'center',
      marginBottom: '40px'
    },
    logo: {
      width: '100px',
      height: '100px',
      borderRadius: '50%',
      background: 'linear-gradient(135deg, #3b82f6 0%, #6366f1 100%)',
      margin: '0 auto 20px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      boxShadow: '0 8px 24px rgba(0, 0, 0, 0.15)'
    },
    title: {
      fontSize: '32px',
      fontWeight: '800',
      marginBottom: '10px',
      background: 'linear-gradient(135deg, #3b82f6 0%, #6366f1 100%)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      lineHeight: '1.2'
    },
    subtitle: {
      fontSize: '17px',
      opacity: 0.8,
      lineHeight: '1.6',
      maxWidth: '400px',
      margin: '0 auto 30px',
      textAlign: 'center'
    },
    formContainer: {
      display: 'flex',
      flexDirection: 'column',
      gap: '30px'
    },
    stepIndicator: {
      display: 'flex',
      justifyContent: 'center',
      marginBottom: '30px',
      position: 'relative'
    },
    step: {
      width: '30px',
      height: '30px',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontWeight: 'bold',
      margin: '0 5px',
      background: isDarkMode ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)',
      color: isDarkMode ? 'rgba(255, 255, 255, 0.5)' : 'rgba(0, 0, 0, 0.5)'
    },
    activeStep: {
      background: 'linear-gradient(135deg, #3b82f6 0%, #6366f1 100%)',
      color: 'white'
    },
    stepLine: {
      position: 'absolute',
      height: '2px',
      background: isDarkMode ? 'rgba(255, 255, 255, 0.2)' : 'rgba(0, 0, 0, 0.1)',
      top: '15px',
      zIndex: 0
    },
    activeStepLine: {
      background: 'linear-gradient(90deg, #3b82f6 0%, #6366f1 100%)'
    },
    formGroup: {
      display: 'flex',
      flexDirection: 'column',
      gap: '8px',
      opacity: 0,
      animation: 'fadeIn 0.5s ease-in forwards',
      animationFillMode: 'both'
    },
    formLabel: {
      fontSize: '16px',
      fontWeight: '600',
      color: isDarkMode ? '#f8fafc' : '#1e293b'
    },
    formInput: {
      width: '100%',
      padding: '14px 20px',
      borderRadius: '12px',
      border: isDarkMode ? '1px solid rgba(255, 255, 255, 0.2)' : '1px solid rgba(0, 0, 0, 0.1)',
      background: isDarkMode ? 'rgba(30, 41, 59, 0.7)' : 'rgba(248, 250, 252, 0.7)',
      color: isDarkMode ? '#f8fafc' : '#1e293b',
      fontSize: '15px',
      outline: 'none',
      transition: 'all 0.2s ease',
      ':focus': {
        borderColor: '#3b82f6',
        boxShadow: '0 0 0 3px rgba(59, 130, 246, 0.2)'
      }
    },
    button: {
      padding: '16px 24px',
      borderRadius: '30px',
      border: 'none',
      background: 'linear-gradient(135deg, #3b82f6 0%, #6366f1 100%)',
      color: 'white',
      cursor: 'pointer',
      fontSize: '16px',
      fontWeight: '600',
      transition: 'all 0.2s ease',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '8px',
      marginTop: '20px',
      ':hover': {
        transform: 'translateY(-2px)',
        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
      }
    },
    iconContainer: {
      display: 'flex',
      justifyContent: 'center',
      margin: '20px 0',
      gap: '20px'
    },
    iconCircle: {
      width: '60px',
      height: '60px',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      background: isDarkMode ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.05)'
    },
    footer: {
      position: 'absolute',
      bottom: '20px',
      left: 0,
      right: 0,
      textAlign: 'center',
      fontSize: '13px',
      opacity: 0.6,
      color: isDarkMode ? '#f8fafc' : '#1e293b'
    },
    backgroundElements: {
      position: 'absolute',
      width: '100%',
      height: '100%',
      overflow: 'hidden',
      zIndex: 1,
      top: 0,
      left: 0
    },
    bubble: {
      position: 'absolute',
      borderRadius: '50%',
      background: isDarkMode ? 'rgba(59, 130, 246, 0.1)' : 'rgba(59, 130, 246, 0.05)',
      filter: 'blur(40px)',
      animation: 'float 15s infinite ease-in-out'
    }
  };

  const handleNext = () => {
    if (currentStep < 3) {
      setCurrentStep(currentStep + 1);
    } else {
      onSubmit(userProfile);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserProfile(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const isFormValid = () => {
    if (currentStep === 1) return userProfile.name.trim() !== '';
    return true;
  };

  return (
    <div style={styles.container}>
      <style>
        {`
          @keyframes fadeIn {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
          }
          @keyframes float {
            0% { transform: translateY(0) translateX(0); }
            50% { transform: translateY(-20px) translateX(20px); }
            100% { transform: translateY(0) translateX(0); }
          }
          @keyframes pulse {
            0% { transform: scale(1); opacity: 0.7; }
            50% { transform: scale(1.05); opacity: 1; }
            100% { transform: scale(1); opacity: 0.7; }
          }
        `}
      </style>

      {/* Background decorative elements */}
      <div style={styles.backgroundElements}>
        <div style={{...styles.bubble, width: '300px', height: '300px', top: '-100px', left: '-100px'}}></div>
        <div style={{...styles.bubble, width: '200px', height: '200px', bottom: '-50px', right: '-50px'}}></div>
        <div style={{...styles.bubble, width: '150px', height: '150px', top: '50%', left: '10%', animationDelay: '2s'}}></div>
        <div style={{...styles.bubble, width: '250px', height: '250px', bottom: '20%', right: '15%', animationDelay: '4s'}}></div>
      </div>

      <div style={styles.card}>
        <div style={styles.header}>
          <div style={styles.logo}>
            <Smile size={48} color="white" />
          </div>
          <h1 style={styles.title}>Welcome to Buddy!</h1>
          <p style={styles.subtitle}>
            {currentStep === 1 && "Let's start by getting to know each other!"}
            {currentStep === 2 && "Tell me about your interests and hobbies!"}
            {currentStep === 3 && "Almost done! Share some of your favorites with me."}
          </p>

          <div style={styles.stepIndicator}>
            {[1, 2, 3].map((step) => (
              <React.Fragment key={step}>
                <div style={{
                  ...styles.step,
                  ...(step <= currentStep ? styles.activeStep : {}),
                  ...(step === currentStep ? { animation: 'pulse 2s infinite' } : {})
                }}>
                  {step}
                </div>
                {step < 3 && (
                  <div style={{
                    ...styles.stepLine,
                    ...(step < currentStep ? styles.activeStepLine : {}),
                    width: '60px',
                    left: step === 1 ? 'calc(50% + 20px)' : step === 2 ? 'calc(50% + 70px)' : 'calc(50% + 120px)'
                  }}></div>
                )}
              </React.Fragment>
            ))}
          </div>
        </div>

        <div style={styles.formContainer}>
          {/* Step 1: Name */}
          {currentStep === 1 && (
            <div style={{...styles.formGroup, animationDelay: '0.2s'}}>
              <label style={styles.formLabel}>
                What should I call you? *
              </label>
              <input
                type="text"
                name="name"
                value={userProfile.name}
                onChange={handleInputChange}
                style={styles.formInput}
                placeholder="Your name or nickname"
                autoFocus
              />
              <div style={styles.iconContainer}>
                <div style={styles.iconCircle}>
                  <User size={24} color={isDarkMode ? 'rgba(255, 255, 255, 0.8)' : 'rgba(0, 0, 0, 0.8)'} />
                </div>
              </div>
            </div>
          )}

          {/* Step 2: Hobbies */}
          {currentStep === 2 && (
            <div style={{...styles.formGroup, animationDelay: '0.2s'}}>
              <label style={styles.formLabel}>
                What are you into?
              </label>
              <input
                type="text"
                name="hobbies"
                value={userProfile.hobbies}
                onChange={handleInputChange}
                style={styles.formInput}
                placeholder="Gaming, music, sports, cooking, reading..."
                autoFocus
              />
              <div style={styles.iconContainer}>
                <div style={styles.iconCircle}>
                  <Music size={24} color={isDarkMode ? 'rgba(255, 255, 255, 0.8)' : 'rgba(0, 0, 0, 0.8)'} />
                </div>
                <div style={styles.iconCircle}>
                  <Book size={24} color={isDarkMode ? 'rgba(255, 255, 255, 0.8)' : 'rgba(0, 0, 0, 0.8)'} />
                </div>
                <div style={styles.iconCircle}>
                  <Gamepad size={24} color={isDarkMode ? 'rgba(255, 255, 255, 0.8)' : 'rgba(0, 0, 0, 0.8)'} />
                </div>
              </div>
            </div>
          )}

          {/* Step 3: Favorites */}
          {currentStep === 3 && (
            <div style={{...styles.formGroup, animationDelay: '0.2s'}}>
              <label style={styles.formLabel}>
                What are some of your favorites?
              </label>
              <input
                type="text"
                name="favorites"
                value={userProfile.favorites}
                onChange={handleInputChange}
                style={styles.formInput}
                placeholder="Movies, music, books, foods, etc."
                autoFocus
              />
              <div style={styles.iconContainer}>
                <div style={styles.iconCircle}>
                  <Heart size={24} color={isDarkMode ? 'rgba(255, 255, 255, 0.8)' : 'rgba(0, 0, 0, 0.8)'} />
                </div>
              </div>
            </div>
          )}

          <button
            onClick={handleNext}
            disabled={currentStep === 1 && !userProfile.name.trim()}
            style={{
              ...styles.button,
              opacity: (currentStep === 1 && !userProfile.name.trim()) ? 0.6 : 1,
              cursor: (currentStep === 1 && !userProfile.name.trim()) ? 'not-allowed' : 'pointer',
              marginTop: '30px'
            }}
          >
            {currentStep < 3 ? (
              <>
                Continue
                <ArrowRight size={18} />
              </>
            ) : (
              <>
                <Heart size={18} />
                Let's be friends!
              </>
            )}
          </button>
        </div>

        <div style={styles.footer}>
          {currentStep === 1 && "We'll keep your information private and secure"}
          {currentStep === 2 && "This helps me understand you better!"}
          {currentStep === 3 && "Ready to start our friendship!"}
        </div>
      </div>
    </div>
  );
};

export default BuddyOnboarding;
