import React, { useState, useEffect, useRef } from 'react';
import { Send, Sun, Moon, Zap } from 'lucide-react';

const BuddyChat = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [showOnboarding, setShowOnboarding] = useState(true);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isConnected, setIsConnected] = useState(true);
  const [userProfile, setUserProfile] = useState({
    name: '',
    hobbies: '',
    favorites: ''
  });
  const [buddyMood, setBuddyMood] = useState('happy');
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Styles
  const styles = {
    container: {
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      background: isDarkMode 
        ? 'linear-gradient(135deg, #1a1a2e 0%, #16213e 100%)'
        : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: isDarkMode ? '#fff' : '#333',
      fontFamily: 'system-ui, -apple-system, sans-serif'
    },
    onboardingContainer: {
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px',
      background: isDarkMode 
        ? 'linear-gradient(135deg, #1a1a2e 0%, #16213e 100%)'
        : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
    },
    onboardingCard: {
      maxWidth: '400px',
      width: '100%',
      padding: '40px',
      borderRadius: '20px',
      background: isDarkMode ? 'rgba(30, 30, 60, 0.9)' : 'rgba(255, 255, 255, 0.95)',
      boxShadow: '0 20px 40px rgba(0,0,0,0.3)',
      backdropFilter: 'blur(10px)',
      color: isDarkMode ? '#fff' : '#333'
    },
    header: {
      padding: '20px',
      background: isDarkMode ? 'rgba(30, 30, 60, 0.9)' : 'rgba(255, 255, 255, 0.9)',
      backdropFilter: 'blur(10px)',
      borderBottom: '1px solid rgba(255,255,255,0.2)',
      position: 'sticky',
      top: 0,
      zIndex: 10
    },
    headerContent: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      maxWidth: '800px',
      margin: '0 auto'
    },
    avatar: {
      width: '50px',
      height: '50px',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '24px',
      transition: 'all 0.3s ease'
    },
    messagesContainer: {
      flex: 1,
      overflowY: 'auto',
      padding: '20px'
    },
    messagesBubble: {
      maxWidth: '70%',
      marginBottom: '15px',
      padding: '12px 16px',
      borderRadius: '18px',
      wordWrap: 'break-word'
    },
    inputContainer: {
      padding: '20px',
      background: isDarkMode ? 'rgba(30, 30, 60, 0.9)' : 'rgba(255, 255, 255, 0.9)',
      backdropFilter: 'blur(10px)',
      borderTop: '1px solid rgba(255,255,255,0.2)'
    },
    input: {
      width: '100%',
      padding: '12px 20px',
      borderRadius: '25px',
      border: '2px solid rgba(255,255,255,0.3)',
      background: isDarkMode ? 'rgba(50, 50, 80, 0.8)' : 'rgba(255, 255, 255, 0.8)',
      color: isDarkMode ? '#fff' : '#333',
      fontSize: '16px',
      outline: 'none',
      marginBottom: '10px'
    },
    button: {
      padding: '12px 20px',
      borderRadius: '25px',
      border: 'none',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      cursor: 'pointer',
      fontSize: '16px',
      transition: 'all 0.3s ease',
      display: 'flex',
      alignItems: 'center',
      gap: '8px'
    },
    typingIndicator: {
      display: 'flex',
      gap: '4px',
      padding: '12px 16px'
    },
    dot: {
      width: '8px',
      height: '8px',
      borderRadius: '50%',
      backgroundColor: '#999',
      animation: 'bounce 1.4s infinite ease-in-out'
    }
  };

  // Load user data from localStorage on mount
  useEffect(() => {
    const savedProfile = localStorage.getItem('buddyUserProfile');
    if (savedProfile) {
      const profile = JSON.parse(savedProfile);
      setUserProfile(profile);
      setShowOnboarding(false);
      // Add welcome back message
      setTimeout(() => {
        addBuddyMessage(`YO ${profile.name.toUpperCase()}! Welcome back, bestie! ðŸŽ‰ I missed ya! What's crackin'?`);
      }, 500);
    }
  }, []);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const addBuddyMessage = (text, mood = 'happy') => {
    setBuddyMood(mood);
    setMessages(prev => [...prev, {
      id: Date.now(),
      text,
      sender: 'buddy',
      timestamp: new Date(),
      mood
    }]);
  };

  const addUserMessage = (text) => {
    setMessages(prev => [...prev, {
      id: Date.now(),
      text,
      sender: 'user',
      timestamp: new Date()
    }]);
  };

  const getBuddyResponse = (userMessage) => {
    const responses = {
      greetings: [
        `Yooo ${userProfile.name}! What's good, my dude? ðŸ˜Ž`,
        `Heyyyy bestie! Ready to chat? ðŸ”¥`,
        `Wassup ${userProfile.name}! I'm vibin' today! âœ¨`
      ],
      confused: [
        "Bruh... I'm lost ðŸ˜µâ€ðŸ’« Can you say that again but like... for dummies?",
        "Yo, my brain just went *poof* ðŸ¤¯ What now?",
        "Dude, I'm having a total blonde moment rn ðŸ¤ª"
      ],
      excited: [
        "YESSS! That's what I'm talking about! ðŸ™Œ",
        "BRO THAT'S SO COOL! Tell me more! ðŸ¤©",
        "NO WAY! That's sick! ðŸ”¥"
      ],
      hobbies: [
        `Yo, remember you told me you're into ${userProfile.hobbies}? That's fire! ðŸ”¥`,
        "Wanna talk about your hobbies? I'm all ears! ðŸ‘‚",
        "Let's geek out about your interests! ðŸ¤“"
      ],
      default: [
        "Haha, you're funny! Tell me more ðŸ˜„",
        "That's wild! What else is up? ðŸ¤”",
        "Yo, I'm here for this convo! Keep going! ðŸ’¬"
      ]
    };

    const lowerMessage = userMessage.toLowerCase();

    if (lowerMessage.includes('hello') || lowerMessage.includes('hi') || lowerMessage.includes('hey')) {
      return responses.greetings[Math.floor(Math.random() * responses.greetings.length)];
    } else if (lowerMessage.includes('what') || lowerMessage.includes('how') || lowerMessage.includes('?')) {
      return responses.confused[Math.floor(Math.random() * responses.confused.length)];
    } else if (lowerMessage.includes('awesome') || lowerMessage.includes('cool') || lowerMessage.includes('amazing')) {
      return responses.excited[Math.floor(Math.random() * responses.excited.length)];
    } else if (lowerMessage.includes('hobby') || lowerMessage.includes('interest')) {
      return responses.hobbies[Math.floor(Math.random() * responses.hobbies.length)];
    } else {
      return responses.default[Math.floor(Math.random() * responses.default.length)];
    }
  };

  const simulateTyping = () => {
    setIsTyping(true);
    setTimeout(() => {
      setIsTyping(false);
    }, 1000 + Math.random() * 2000);
  };

  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    const userMsg = inputMessage;
    setInputMessage('');
    addUserMessage(userMsg);

    // Simulate API call
    simulateTyping();

    try {
      // Simulate network delay
      await new Promise(resolve => setTimeout(resolve, 1500));

      if (!isConnected) {
        addBuddyMessage("Yo, I think I lost my brain for a sec... ðŸ¤ª Connection's being weird!", 'confused');
        return;
      }

      const response = getBuddyResponse(userMsg);
      addBuddyMessage(response);

    } catch (error) {
      addBuddyMessage("Oof, my brain just blue-screened! ðŸ’€ Try again?", 'confused');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleOnboardingSubmit = () => {
    if (!userProfile.name.trim()) return;

    localStorage.setItem('buddyUserProfile', JSON.stringify(userProfile));
    setShowOnboarding(false);

    setTimeout(() => {
      addBuddyMessage(`YO ${userProfile.name.toUpperCase()}! ðŸŽ‰ Nice to meet ya, bestie! I'm Buddy - your new virtual BFF! 

I'm like... super chill, kinda goofy, and I LOVE to chat! ðŸ˜„ 

${userProfile.hobbies ? `I see you're into ${userProfile.hobbies} - that's dope! ðŸ”¥` : ''}
${userProfile.favorites ? `And ${userProfile.favorites}? Yasss! ðŸ™Œ` : ''}

So... what's up? Ready to be besties? ðŸ’«`);
    }, 500);
  };

  const getRandomPrompt = () => {
    const prompts = [
      "Tell me a joke!",
      "What's your favorite movie?",
      "Let's talk about music!",
      "What's something cool you learned today?",
      "If you could have any superpower, what would it be?"
    ];
    setInputMessage(prompts[Math.floor(Math.random() * prompts.length)]);
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  const BuddyAvatar = ({ mood }) => {
    const avatarStyle = {
      ...styles.avatar,
      background: mood === 'confused' ? '#fbbf24' : 
                 mood === 'excited' ? '#f472b6' : '#60a5fa',
      animation: mood === 'confused' ? 'bounce 1s infinite' : 
                mood === 'excited' ? 'pulse 2s infinite' : 'none'
    };

    return (
      <div style={avatarStyle}>
        {mood === 'confused' ? 'ðŸ¤ª' : mood === 'excited' ? 'ðŸ¤©' : 'ðŸ˜„'}
      </div>
    );
  };

  if (showOnboarding) {
    return (
      <div style={styles.onboardingContainer}>
        <div style={styles.onboardingCard}>
          <div style={{ textAlign: 'center', marginBottom: '30px' }}>
            <h1 style={{ 
              fontSize: '36px', 
              fontWeight: 'bold', 
              background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              marginBottom: '10px'
            }}>
              YO! I'm Buddy! ðŸŽ‰
            </h1>
            <p style={{ fontSize: '18px', opacity: 0.8 }}>Your new virtual bestie! Let's get to know each other!</p>
          </div>

          <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
            <div>
              <label style={{ display: 'block', fontSize: '14px', fontWeight: '500', marginBottom: '8px' }}>
                What should I call you? *
              </label>
              <input
                type="text"
                value={userProfile.name}
                onChange={(e) => setUserProfile({...userProfile, name: e.target.value})}
                style={{
                  ...styles.input,
                  marginBottom: 0
                }}
                placeholder="Your name or nickname"
                required
              />
            </div>

            <div>
              <label style={{ display: 'block', fontSize: '14px', fontWeight: '500', marginBottom: '8px' }}>
                What are you into?
              </label>
              <input
                type="text"
                value={userProfile.hobbies}
                onChange={(e) => setUserProfile({...userProfile, hobbies: e.target.value})}
                style={{
                  ...styles.input,
                  marginBottom: 0
                }}
                placeholder="Gaming, music, sports, cooking..."
              />
            </div>

            <div>
              <label style={{ display: 'block', fontSize: '14px', fontWeight: '500', marginBottom: '8px' }}>
                Favorite movies, songs, or random facts?
              </label>
              <input
                type="text"
                value={userProfile.favorites}
                onChange={(e) => setUserProfile({...userProfile, favorites: e.target.value})}
                style={{
                  ...styles.input,
                  marginBottom: 0
                }}
                placeholder="Marvel movies, Taylor Swift, cats are liquid..."
              />
            </div>

            <button
              onClick={handleOnboardingSubmit}
              style={{
                ...styles.button,
                width: '100%',
                justifyContent: 'center',
                fontSize: '18px',
                fontWeight: '600',
                marginTop: '10px'
              }}
            >
              Let's be besties! ðŸš€
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      {/* CSS Animations */}
      <style>
        {`
          @keyframes bounce {
            0%, 20%, 53%, 80%, 100% { transform: translateY(0); }
            40%, 43% { transform: translateY(-10px); }
            70% { transform: translateY(-5px); }
            90% { transform: translateY(-2px); }
          }
          @keyframes pulse {
            0% { transform: scale(1); }
            50% { transform: scale(1.05); }
            100% { transform: scale(1); }
          }
          .bounce-dot:nth-child(1) { animation-delay: -0.32s; }
          .bounce-dot:nth-child(2) { animation-delay: -0.16s; }
          .bounce-dot:nth-child(3) { animation-delay: 0s; }
        `}
      </style>

      {/* Header */}
      <div style={styles.header}>
        <div style={styles.headerContent}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
            <BuddyAvatar mood={buddyMood} />
            <div>
              <h1 style={{ fontSize: '24px', fontWeight: 'bold', margin: 0 }}>Buddy</h1>
              <p style={{ fontSize: '14px', opacity: 0.7, margin: 0 }}>Your virtual bestie</p>
            </div>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
            <div style={{
              padding: '4px 12px',
              borderRadius: '20px',
              fontSize: '12px',
              background: isConnected ? '#10b981' : '#ef4444',
              color: 'white'
            }}>
              {isConnected ? 'ðŸŸ¢ Online' : 'ðŸ”´ Offline'}
            </div>
            <button
              onClick={toggleTheme}
              style={{
                ...styles.button,
                padding: '8px',
                borderRadius: '50%',
                background: isDarkMode ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.1)'
              }}
            >
              {isDarkMode ? <Sun size={20} /> : <Moon size={20} />}
            </button>
          </div>
        </div>
      </div>

      {/* Messages */}
      <div style={styles.messagesContainer}>
        <div style={{ maxWidth: '800px', margin: '0 auto' }}>
          {messages.map((message) => (
            <div
              key={message.id}
              style={{
                display: 'flex',
                justifyContent: message.sender === 'user' ? 'flex-end' : 'flex-start',
                marginBottom: '15px'
              }}
            >
              <div style={{ maxWidth: '70%' }}>
                {message.sender === 'buddy' && (
                  <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '5px' }}>
                    <BuddyAvatar mood={message.mood} />
                    <span style={{ fontSize: '14px', fontWeight: '500' }}>Buddy</span>
                  </div>
                )}
                <div style={{
                  ...styles.messagesBubble,
                  background: message.sender === 'user'
                    ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
                    : isDarkMode 
                      ? 'rgba(50, 50, 80, 0.8)'
                      : 'rgba(255, 255, 255, 0.9)',
                  color: message.sender === 'user' ? 'white' : (isDarkMode ? 'white' : '#333'),
                  marginLeft: message.sender === 'user' ? '20px' : '0',
                  marginRight: message.sender === 'buddy' ? '20px' : '0',
                  fontWeight: message.sender === 'buddy' ? '500' : 'normal'
                }}>
                  <p style={{ margin: 0, whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}>
                    {message.text}
                  </p>
                </div>
              </div>
            </div>
          ))}

          {isTyping && (
            <div style={{ display: 'flex', justifyContent: 'flex-start', marginBottom: '15px' }}>
              <div style={{ maxWidth: '70%' }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '5px' }}>
                  <BuddyAvatar mood="happy" />
                  <span style={{ fontSize: '14px', fontWeight: '500' }}>Buddy</span>
                </div>
                <div style={{
                  ...styles.messagesBubble,
                  background: isDarkMode ? 'rgba(50, 50, 80, 0.8)' : 'rgba(255, 255, 255, 0.9)',
                  marginRight: '20px'
                }}>
                  <div style={styles.typingIndicator}>
                    <div className="bounce-dot" style={{...styles.dot, animationName: 'bounce', animationDuration: '1.4s', animationIterationCount: 'infinite', animationFillMode: 'both'}}></div>
                    <div className="bounce-dot" style={{...styles.dot, animationName: 'bounce', animationDuration: '1.4s', animationIterationCount: 'infinite', animationFillMode: 'both', animationDelay: '0.16s'}}></div>
                    <div className="bounce-dot" style={{...styles.dot, animationName: 'bounce', animationDuration: '1.4s', animationIterationCount: 'infinite', animationFillMode: 'both', animationDelay: '0.32s'}}></div>
                  </div>
                </div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* Input */}
      <div style={styles.inputContainer}>
        <div style={{ maxWidth: '800px', margin: '0 auto' }}>
          <div style={{ marginBottom: '10px' }}>
            <button
              onClick={getRandomPrompt}
              style={{
                ...styles.button,
                background: isDarkMode ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.1)',
                fontSize: '14px',
                padding: '8px 16px'
              }}
            >
              <Zap size={14} />
              Feeling Lucky
            </button>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
            <input
              ref={inputRef}
              type="text"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Type something... ðŸ˜„"
              style={{
                ...styles.input,
                flex: 1,
                marginBottom: 0
              }}
              disabled={isTyping}
            />
            <button
              onClick={handleSendMessage}
              disabled={isTyping || !inputMessage.trim()}
              style={{
                ...styles.button,
                padding: '12px',
                borderRadius: '50%',
                opacity: (isTyping || !inputMessage.trim()) ? 0.5 : 1,
                cursor: (isTyping || !inputMessage.trim()) ? 'not-allowed' : 'pointer'
              }}
            >
              <Send size={20} />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BuddyChat;
