import React, { useState, useEffect, useRef } from 'react';
import {
  Send, Sun, Moon, Zap, Heart, Smile, Laugh, User, MessageSquare,
  Settings, HelpCircle, Edit, X, Check, ChevronLeft, ArrowRight
} from 'lucide-react';
import BuddyOnboarding from './components/onboarding/BuddyOnboarding';

const BuddyChat = () => {
  const colors = {
    primary: '#6366f1',
    secondary: '#8b5cf6',
    accent: '#ec4899',
    darkBg: '#0f172a',
    lightBg: '#f8fafc',
    darkCard: '#1e293b',
    lightCard: '#ffffff',
    success: '#10b981',
    warning: '#f59e0b',
    error: '#ef4444',
  };

  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [showOnboarding, setShowOnboarding] = useState(true);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isConnected, setIsConnected] = useState(true);
  const [userProfile, setUserProfile] = useState({
    name: '',
    hobbies: '',
    favorites: ''
  });
  const [buddyMood, setBuddyMood] = useState('happy');
  const [showSidebar, setShowSidebar] = useState(true);
  const [activeView, setActiveView] = useState('chat');
  const [tempProfile, setTempProfile] = useState({ ...userProfile });
  const messagesEndRef = useRef(null);
  const messagesContainerRef = useRef(null);
  const inputRef = useRef(null);

  // Helper function to convert hex to rgb
  const hexToRgb = (hex) => {
    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? `${parseInt(result[1], 16)}, ${parseInt(result[2], 16)}, ${parseInt(result[3], 16)}` : '0, 0, 0';
  };

  // Enhanced mood states with better colors
  const moodStates = {
    happy: { emoji: 'ðŸ˜Š', color: colors.primary, animation: 'none' },
    excited: { emoji: 'ðŸ¤©', color: colors.accent, animation: 'pulse' },
    confused: { emoji: 'ðŸ¤”', color: colors.warning, animation: 'bounce' },
    thinking: { emoji: 'ðŸ¤¨', color: colors.secondary, animation: 'float' },
    sad: { emoji: 'ðŸ˜¢', color: colors.error, animation: 'none' }
  };

  // Styles
  const styles = {
    container: {
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      background: isDarkMode
        ? `linear-gradient(135deg, ${colors.darkBg} 0%, ${colors.darkCard} 100%)`
        : `linear-gradient(135deg, ${colors.lightBg} 0%, #e2e8f0 100%)`,
      color: isDarkMode ? colors.lightBg : colors.darkBg,
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
      transition: 'all 0.3s ease',
      position: 'relative',
      overflow: 'hidden'
    },
    appContainer: {
      display: 'flex',
      flex: 1,
      height: '100vh',
      overflow: 'hidden',
    },
    sidebar: {
      width: showSidebar ? '280px' : '70px',
      background: isDarkMode ? `rgba(${hexToRgb(colors.darkCard)}, 0.9)` : `rgba(${hexToRgb(colors.lightCard)}, 0.9)`,
      backdropFilter: 'blur(10px)',
      borderRight: isDarkMode ? `1px solid rgba(${hexToRgb(colors.lightBg)}, 0.1)` : `1px solid rgba(${hexToRgb(colors.darkBg)}, 0.1)`,
      padding: '20px 10px',
      display: 'flex',
      flexDirection: 'column',
      transition: 'width 0.3s ease',
      height: '100vh',
    },
    sidebarCollapsed: {
      width: '70px',
      overflow: 'hidden'
    },
    sidebarItem: {
      display: 'flex',
      alignItems: 'center',
      padding: '12px 15px',
      margin: '5px 0',
      borderRadius: '12px',
      cursor: 'pointer',
      color: isDarkMode ? `rgba(${hexToRgb(colors.lightBg)}, 0.8)` : `rgba(${hexToRgb(colors.darkBg)}, 0.8)`,
      transition: 'all 0.2s ease',
      background: activeView === 'chat' ? (isDarkMode ? `rgba(${hexToRgb(colors.primary)}, 0.2)` : `rgba(${hexToRgb(colors.primary)}, 0.1)`) : 'transparent',
      ':hover': {
        background: isDarkMode ? `rgba(${hexToRgb(colors.lightBg)}, 0.1)` : `rgba(${hexToRgb(colors.darkBg)}, 0.05)`
      }
    },
    sidebarItemExpanded: {
      justifyContent: 'flex-start',
      gap: '12px'
    },
    sidebarItemCollapsed: {
      justifyContent: 'center'
    },
    mainContent: {
      flex: 1,
      display: 'flex',
      flexDirection: 'column',
      overflow: 'hidden',
      height: '100vh'
    },
    header: {
      padding: '15px 20px',
      background: isDarkMode ? `rgba(${hexToRgb(colors.darkCard)}, 0.9)` : `rgba(${hexToRgb(colors.lightCard)}, 0.9)`,
      backdropFilter: 'blur(10px)',
      borderBottom: isDarkMode ? `1px solid rgba(${hexToRgb(colors.lightBg)}, 0.1)` : `1px solid rgba(${hexToRgb(colors.darkBg)}, 0.1)`,
      position: 'sticky',
      top: 0,
      zIndex: 10,
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    headerContent: {
      display: 'flex',
      alignItems: 'center',
      gap: '15px',
      width: '100%'
    },
    avatar: {
      width: '50px',
      height: '50px',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '24px',
      transition: 'all 0.3s ease',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
    },
    messagesContainer: {
      flex: 1,
      overflowY: 'auto',
      padding: '20px',
      background: isDarkMode ? `rgba(${hexToRgb(colors.darkCard)}, 0.5)` : `rgba(${hexToRgb(colors.lightCard)}, 0.5)`,
      display: 'flex',
      flexDirection: 'column',
      height: 'calc(100vh - 180px)',
      overflowX: 'hidden'
    },
    messagesContent: {
      maxWidth: '800px',
      margin: '0 auto',
      width: '100%',
      display: 'flex',
      flexDirection: 'column',
      paddingBottom: '20px'
    },
    inputContainer: {
      padding: '20px',
      background: isDarkMode ? `rgba(${hexToRgb(colors.darkCard)}, 0.9)` : `rgba(${hexToRgb(colors.lightCard)}, 0.9)`,
      backdropFilter: 'blur(10px)',
      borderTop: isDarkMode ? `1px solid rgba(${hexToRgb(colors.lightBg)}, 0.1)` : `1px solid rgba(${hexToRgb(colors.darkBg)}, 0.1)`,
      position: 'sticky',
      bottom: 0,
      zIndex: 10
    },
    input: {
      width: '100%',
      padding: '14px 20px',
      borderRadius: '30px',
      border: isDarkMode ? `1px solid rgba(${hexToRgb(colors.lightBg)}, 0.2)` : `1px solid rgba(${hexToRgb(colors.darkBg)}, 0.1)`,
      background: isDarkMode ? `rgba(${hexToRgb(colors.darkCard)}, 0.8)` : `rgba(${hexToRgb(colors.lightCard)}, 0.9)`,
      color: isDarkMode ? colors.lightBg : colors.darkBg,
      fontSize: '15px',
      outline: 'none',
      marginBottom: '10px',
      transition: 'all 0.2s ease',
      ':focus': {
        borderColor: colors.primary,
        boxShadow: `0 0 0 3px rgba(${hexToRgb(colors.primary)}, 0.2)`
      }
    },
    button: {
      padding: '12px 20px',
      borderRadius: '30px',
      border: 'none',
      background: `linear-gradient(135deg, ${colors.primary} 0%, ${colors.secondary} 100%)`,
      color: colors.lightBg,
      cursor: 'pointer',
      fontSize: '15px',
      fontWeight: '500',
      transition: 'all 0.2s ease',
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      ':hover': {
        opacity: 0.9,
        transform: 'translateY(-1px)'
      },
      ':active': {
        transform: 'translateY(0)'
      }
    },
    settingsContainer: {
      maxWidth: '600px',
      margin: '0 auto',
      width: '100%',
      padding: '20px',
      background: isDarkMode ? `rgba(${hexToRgb(colors.darkCard)}, 0.8)` : `rgba(${hexToRgb(colors.lightCard)}, 0.8)`,
      borderRadius: '16px',
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)',
      backdropFilter: 'blur(8px)'
    },
    settingsHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '20px',
      paddingBottom: '15px',
      borderBottom: isDarkMode ? `1px solid rgba(${hexToRgb(colors.lightBg)}, 0.1)` : `1px solid rgba(${hexToRgb(colors.darkBg)}, 0.1)`
    },
    settingsTitle: {
      fontSize: '22px',
      fontWeight: '700',
      margin: 0,
      color: isDarkMode ? colors.lightBg : colors.darkBg
    },
    settingsForm: {
      display: 'flex',
      flexDirection: 'column',
      gap: '20px'
    },
    formGroup: {
      display: 'flex',
      flexDirection: 'column',
      gap: '8px'
    },
    formLabel: {
      fontSize: '15px',
      fontWeight: '600',
      color: isDarkMode ? colors.lightBg : colors.darkBg
    },
    formInput: {
      width: '100%',
      padding: '12px 16px',
      borderRadius: '12px',
      border: isDarkMode ? `1px solid rgba(${hexToRgb(colors.lightBg)}, 0.2)` : `1px solid rgba(${hexToRgb(colors.darkBg)}, 0.1)`,
      background: isDarkMode ? `rgba(${hexToRgb(colors.darkCard)}, 0.7)` : `rgba(${hexToRgb(colors.lightCard)}, 0.7)`,
      color: isDarkMode ? colors.lightBg : colors.darkBg,
      fontSize: '15px',
      outline: 'none'
    },
    settingsActions: {
      display: 'flex',
      justifyContent: 'flex-end',
      gap: '10px',
      marginTop: '30px'
    },
    secondaryButton: {
      padding: '10px 20px',
      borderRadius: '30px',
      border: isDarkMode ? `1px solid rgba(${hexToRgb(colors.lightBg)}, 0.2)` : `1px solid rgba(${hexToRgb(colors.darkBg)}, 0.1)`,
      background: 'transparent',
      color: isDarkMode ? colors.lightBg : colors.darkBg,
      cursor: 'pointer',
      fontSize: '15px',
      fontWeight: '500',
      transition: 'all 0.2s ease'
    }
  };

  // Load user data from localStorage on mount
  useEffect(() => {
    const savedProfile = localStorage.getItem('buddyUserProfile');
    if (savedProfile) {
      const profile = JSON.parse(savedProfile);
      setUserProfile(profile);
      setTempProfile(profile);
      setShowOnboarding(false);
    }

    // Check for saved theme preference
    const savedTheme = localStorage.getItem('buddyThemePreference');
    if (savedTheme) {
      setIsDarkMode(savedTheme === 'dark');
    }
  }, []);

  // Save theme preference when it changes
  useEffect(() => {
    localStorage.setItem('buddyThemePreference', isDarkMode ? 'dark' : 'light');
  }, [isDarkMode]);

  // Auto-scroll only the messages container
  useEffect(() => {
    if (messagesContainerRef.current) {
      messagesContainerRef.current.scrollTo({
        top: messagesContainerRef.current.scrollHeight,
        behavior: 'smooth'
      });
    }
  }, [messages, isTyping]);

  const addBuddyMessage = (text, mood = 'happy') => {
    setBuddyMood(mood);
    setMessages(prev => [...prev, {
      id: Date.now(),
      text,
      sender: 'buddy',
      timestamp: new Date(),
      mood
    }]);
  };

  const addUserMessage = (text) => {
    setMessages(prev => [...prev, {
      id: Date.now(),
      text,
      sender: 'user',
      timestamp: new Date()
    }]);
  };

  const getBuddyResponse = (userMessage) => {
    const lowerMessage = userMessage.toLowerCase();
    const responses = {
      greetings: [
        `Hey there ${userProfile.name}! ðŸ‘‹ What's up? How's your day going so far?`,
        `Hi ${userProfile.name}! ðŸ˜Š So good to hear from you! What's on your mind today?`,
        `Hello ${userProfile.name}! ðŸŒŸ You're looking awesome today! What can I do for you?`,
        `Well hello there, ${userProfile.name}! ðŸ¤— I was just thinking about you! What brings you here today?`
      ],
      confused: [
        "Hmm, I'm not sure I understand. Could you explain that a bit more? I really want to get what you're saying!",
        "I think I might have missed something there. Could you try saying that in a different way?",
        "I'm a little confused, but I want to understand! Could you give me more details?",
        "I'm not quite following. Maybe we could talk about something else? What do you like to do for fun?"
      ],
      excited: [
        "OMG that's so exciting! ðŸŽ‰ Tell me more about that! I love hearing good news!",
        "Wow, that sounds amazing! ðŸ¤© I'm so happy for you! What else is going great in your life?",
        "That's incredible! You're making my day better just by sharing that! ðŸ’– What other awesome things have been happening?",
        "No way! That's so cool! ðŸ˜ I wish I could high-five you right now! What's the story behind that?"
      ],
      hobbies: [
        `You know, I was just thinking about ${userProfile.hobbies}. What do you love most about it?`,
        `I've been meaning to ask - how did you get into ${userProfile.hobbies}? It sounds so interesting!`,
        `Tell me more about ${userProfile.hobbies}! What's the most fun part about it for you?`,
        `I bet you're really good at ${userProfile.hobbies}! What's your favorite memory related to that?`
      ],
      favorites: [
        `Oh, I remember you love ${userProfile.favorites}! What's your favorite thing about that?`,
        `You've mentioned ${userProfile.favorites} before. What's something new you've discovered about it lately?`,
        `Tell me more about why you love ${userProfile.favorites}! I'd love to understand what makes it special to you.`,
        `If you could only keep one thing about ${userProfile.favorites}, what would it be?`
      ],
      default: [
        "That's interesting! What else would you like to talk about?",
        "I see. How does that make you feel?",
        "Tell me more about that! I'm really interested in what you have to say.",
        "That's cool! What other things have been on your mind lately?",
        "I appreciate you sharing that with me. Is there anything else you'd like to discuss?"
      ],
      questions: [
        "That's a great question! Let me think about that for a moment...",
        "Hmm, interesting question! What do you think about it?",
        "I love that you're asking questions! It shows you're really engaged. What are your thoughts?",
        "You know, I'm not sure I have the perfect answer, but I'd love to explore that with you! What are your ideas?"
      ]
    };

    if (lowerMessage.includes('hello') || lowerMessage.includes('hi') || lowerMessage.includes('hey') || lowerMessage.includes('greetings')) {
      return responses.greetings[Math.floor(Math.random() * responses.greetings.length)];
    } else if (lowerMessage.includes('what') || lowerMessage.includes('how') || lowerMessage.includes('why') || lowerMessage.includes('?')) {
      return responses.questions[Math.floor(Math.random() * responses.questions.length)];
    } else if (lowerMessage.includes('awesome') || lowerMessage.includes('cool') || lowerMessage.includes('amazing') || lowerMessage.includes('great') || lowerMessage.includes('exciting')) {
      return responses.excited[Math.floor(Math.random() * responses.excited.length)];
    } else if (lowerMessage.includes('hobby') || lowerMessage.includes('interest') || lowerMessage.includes('like') || lowerMessage.includes('love')) {
      return responses.hobbies[Math.floor(Math.random() * responses.hobbies.length)];
    } else if (lowerMessage.includes('favorite') || lowerMessage.includes('best') || lowerMessage.includes('top') || lowerMessage.includes('favourites')) {
      return responses.favorites[Math.floor(Math.random() * responses.favorites.length)];
    } else {
      return responses.default[Math.floor(Math.random() * responses.default.length)];
    }
  };

  const simulateTyping = () => {
    setIsTyping(true);
    const typingDuration = 800 + Math.random() * 1700;
    setTimeout(() => {
      setIsTyping(false);
    }, typingDuration);
  };

  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    const userMsg = inputMessage;
    setInputMessage('');
    addUserMessage(userMsg);

    simulateTyping();

    try {
      const delay = 500 + Math.random() * 1000;
      await new Promise(resolve => setTimeout(resolve, delay));

      if (!isConnected) {
        const offlineMessages = [
          "Oh no! It seems like we're having some connection issues. ðŸ˜• Let's try that again in a moment?",
          "Uh oh, I think my brain just went offline for a second! ðŸ¤¯ Could you try sending that again?",
          "Oops! Looks like we got disconnected. ðŸ˜¢ Don't worry, I'm sure we'll reconnect soon!",
          "Hmm, something's not right with our connection. ðŸ¤” Let's take a breath and try again in a bit!"
        ];
        addBuddyMessage(offlineMessages[Math.floor(Math.random() * offlineMessages.length)], 'confused');
        return;
      }

      const response = getBuddyResponse(userMsg);
      const shouldAddFollowUp = Math.random() > 0.6;
      if (shouldAddFollowUp) {
        const followUps = [
          "What else is on your mind today?",
          "That's really interesting! What other thoughts do you have about that?",
          "I love this conversation! What would you like to talk about next?",
          "This has been great! Is there anything else you'd like to share?",
          "You're amazing to talk to! What other topics would you like to explore?"
        ];
        setTimeout(() => {
          addBuddyMessage(followUps[Math.floor(Math.random() * followUps.length)]);
        }, 1000);
      }
      addBuddyMessage(response);
    } catch (error) {
      const errorMessages = [
        "Oh no! Something went wrong on my end. ðŸ˜³ Could you try that again?",
        "I think I just had a little brain freeze! â„ï¸ Let's try that again!",
        "Oops! I must have dropped that thought. ðŸ¤• Could you repeat what you said?",
        "Uh oh, I think I need a quick reboot! ðŸ”„ Let's try that conversation again!"
      ];
      addBuddyMessage(errorMessages[Math.floor(Math.random() * errorMessages.length)], 'confused');
    }
  };

