const BuddyChat = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [showOnboarding, setShowOnboarding] = useState(true);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isConnected, setIsConnected] = useState(true);
  const [userProfile, setUserProfile] = useState({
    name: '',
    hobbies: '',
    favorites: ''
  });
  const [buddyMood, setBuddyMood] = useState('happy');
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Load user data from localStorage on mount
  useEffect(() => {
    const savedProfile = localStorage.getItem('buddyUserProfile');
    if (savedProfile) {
      const profile = JSON.parse(savedProfile);
      setUserProfile(profile);
      setShowOnboarding(false);
      // Add welcome back message
      setTimeout(() => {
        addBuddyMessage(`YO ${profile.name.toUpperCase()}! Welcome back, bestie! ðŸŽ‰ I missed ya! What's crackin'?`);
      }, 500);
    }
  }, []);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const addBuddyMessage = (text, mood = 'happy') => {
    setBuddyMood(mood);
    setMessages(prev => [...prev, {
      id: Date.now(),
      text,
      sender: 'buddy',
      timestamp: new Date(),
      mood
    }]);
  };

  const addUserMessage = (text) => {
    setMessages(prev => [...prev, {
      id: Date.now(),
      text,
      sender: 'user',
      timestamp: new Date()
    }]);
  };

  const getBuddyResponse = (userMessage) => {
    const responses = {
      greetings: [
        `Yooo ${userProfile.name}! What's good, my dude? ðŸ˜Ž`,
        `Heyyyy bestie! Ready to chat? ðŸ”¥`,
        `Wassup ${userProfile.name}! I'm vibin' today! âœ¨`
      ],
      confused: [
        "Bruh... I'm lost ðŸ˜µâ€ðŸ’« Can you say that again but like... for dummies?",
        "Yo, my brain just went *poof* ðŸ¤¯ What now?",
        "Dude, I'm having a total blonde moment rn ðŸ¤ª"
      ],
      excited: [
        "YESSS! That's what I'm talking about! ðŸ™Œ",
        "BRO THAT'S SO COOL! Tell me more! ðŸ¤©",
        "NO WAY! That's sick! ðŸ”¥"
      ],
      hobbies: [
        `Yo, remember you told me you're into ${userProfile.hobbies}? That's fire! ðŸ”¥`,
        "Wanna talk about your hobbies? I'm all ears! ðŸ‘‚",
        "Let's geek out about your interests! ðŸ¤“"
      ],
      default: [
        "Haha, you're funny! Tell me more ðŸ˜„",
        "That's wild! What else is up? ðŸ¤”",
        "Yo, I'm here for this convo! Keep going! ðŸ’¬"
      ]
    };

    const lowerMessage = userMessage.toLowerCase();

    if (lowerMessage.includes('hello') || lowerMessage.includes('hi') || lowerMessage.includes('hey')) {
      return responses.greetings[Math.floor(Math.random() * responses.greetings.length)];
    } else if (lowerMessage.includes('what') || lowerMessage.includes('how') || lowerMessage.includes('?')) {
      return responses.confused[Math.floor(Math.random() * responses.confused.length)];
    } else if (lowerMessage.includes('awesome') || lowerMessage.includes('cool') || lowerMessage.includes('amazing')) {
      return responses.excited[Math.floor(Math.random() * responses.excited.length)];
    } else if (lowerMessage.includes('hobby') || lowerMessage.includes('interest')) {
      return responses.hobbies[Math.floor(Math.random() * responses.hobbies.length)];
    } else {
      return responses.default[Math.floor(Math.random() * responses.default.length)];
    }
  };

  const simulateTyping = () => {
    setIsTyping(true);
    setTimeout(() => {
      setIsTyping(false);
    }, 1000 + Math.random() * 2000);
  };

  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    const userMsg = inputMessage;
    setInputMessage('');
    addUserMessage(userMsg);

    // Simulate API call
    simulateTyping();

    try {
      // Simulate network delay
      await new Promise(resolve => setTimeout(resolve, 1500));

      if (!isConnected) {
        addBuddyMessage("Yo, I think I lost my brain for a sec... ðŸ¤ª Connection's being weird!", 'confused');
        return;
      }

      const response = getBuddyResponse(userMsg);
      addBuddyMessage(response);

    } catch (error) {
      addBuddyMessage("Oof, my brain just blue-screened! ðŸ’€ Try again?", 'confused');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleOnboardingSubmit = () => {
    if (!userProfile.name.trim()) return;

    localStorage.setItem('buddyUserProfile', JSON.stringify(userProfile));
    setShowOnboarding(false);

    setTimeout(() => {
      addBuddyMessage(`YO ${userProfile.name.toUpperCase()}! ðŸŽ‰ Nice to meet ya, bestie! I'm Buddy - your new virtual BFF! 

I'm like... super chill, kinda goofy, and I LOVE to chat! ðŸ˜„ 

${userProfile.hobbies ? `I see you're into ${userProfile.hobbies} - that's dope! ðŸ”¥` : ''}
${userProfile.favorites ? `And ${userProfile.favorites}? Yasss! ðŸ™Œ` : ''}

So... what's up? Ready to be besties? ðŸ’«`);
    }, 500);
  };

  const getRandomPrompt = () => {
    const prompts = [
      "Tell me a joke!",
      "What's your favorite movie?",
      "Let's talk about music!",
      "What's something cool you learned today?",
      "If you could have any superpower, what would it be?"
    ];
    setInputMessage(prompts[Math.floor(Math.random() * prompts.length)]);
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  const BuddyAvatar = ({ mood }) => (
    <div className={`w-10 h-10 rounded-full flex items-center justify-center text-2xl transition-all duration-300 ${
      mood === 'confused' ? 'animate-bounce bg-yellow-400' : 
      mood === 'excited' ? 'animate-pulse bg-pink-400' : 
      'bg-blue-400'
    }`}>
      {mood === 'confused' ? 'ðŸ¤ª' : mood === 'excited' ? 'ðŸ¤©' : 'ðŸ˜„'}
    </div>
  );

  if (showOnboarding) {
    return (
      <div className={`min-h-screen flex items-center justify-center p-4 ${
        isDarkMode ? 'bg-gray-900' : 'bg-gradient-to-br from-purple-400 via-pink-300 to-blue-400'
      }`}>
        <div className={`max-w-md w-full p-8 rounded-3xl shadow-2xl ${
          isDarkMode ? 'bg-gray-800 text-white' : 'bg-white text-gray-800'
        }`}>
          <div className="text-center mb-8">
            <h1 className="text-4xl font-bold bg-gradient-to-r from-purple-600 to-pink-600 bg-clip-text text-transparent mb-2">
              YO! I'm Buddy! ðŸŽ‰
            </h1>
            <p className="text-lg">Your new virtual bestie! Let's get to know each other!</p>
          </div>

          <div className="space-y-6">
            <div>
              <label className="block text-sm font-medium mb-2">What should I call you? *</label>
              <input
                type="text"
                value={userProfile.name}
                onChange={(e) => setUserProfile({...userProfile, name: e.target.value})}
                className={`w-full p-3 rounded-lg border-2 ${
                  isDarkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-purple-200'
                } focus:border-purple-500 focus:outline-none transition-colors`}
                placeholder="Your name or nickname"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">What are you into?</label>
              <input
                type="text"
                value={userProfile.hobbies}
                onChange={(e) => setUserProfile({...userProfile, hobbies: e.target.value})}
                className={`w-full p-3 rounded-lg border-2 ${
                  isDarkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-purple-200'
                } focus:border-purple-500 focus:outline-none transition-colors`}
                placeholder="Gaming, music, sports, cooking..."
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">Favorite movies, songs, or random facts?</label>
              <input
                type="text"
                value={userProfile.favorites}
                onChange={(e) => setUserProfile({...userProfile, favorites: e.target.value})}
                className={`w-full p-3 rounded-lg border-2 ${
                  isDarkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-purple-200'
                } focus:border-purple-500 focus:outline-none transition-colors`}
                placeholder="Marvel movies, Taylor Swift, cats are liquid..."
              />
            </div>

            <button
              onClick={handleOnboardingSubmit}
              className="w-full bg-gradient-to-r from-purple-500 to-pink-500 text-white py-3 rounded-lg font-semibold hover:from-purple-600 hover:to-pink-600 transition-all duration-300 transform hover:scale-105"
            >
              Let's be besties! ðŸš€
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`min-h-screen flex flex-col ${
      isDarkMode ? 'bg-gray-900 text-white' : 'bg-gradient-to-br from-purple-50 via-pink-50 to-blue-50 text-gray-800'
    }`}>
      {/* Header */}
      <div className={`p-4 border-b ${
        isDarkMode ? 'bg-gray-800 border-gray-700' : 'bg-white/70 backdrop-blur-sm border-purple-200'
      } sticky top-0 z-10`}>
        <div className="flex items-center justify-between max-w-4xl mx-auto">
          <div className="flex items-center space-x-3">
            <BuddyAvatar mood={buddyMood} />
            <div>
              <h1 className="text-xl font-bold">Buddy</h1>
              <p className="text-sm opacity-70">Your virtual bestie</p>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <div className={`px-2 py-1 rounded-full text-xs ${
              isConnected ? 'bg-green-500 text-white' : 'bg-red-500 text-white'
            }`}>
              {isConnected ? 'ðŸŸ¢ Online' : 'ðŸ”´ Offline'}
            </div>
            <button
              onClick={toggleTheme}
              className={`p-2 rounded-full ${
                isDarkMode ? 'bg-gray-700 hover:bg-gray-600' : 'bg-white hover:bg-gray-100'
              } transition-colors`}
            >
              {isDarkMode ? <Sun size={20} /> : <Moon size={20} />}
            </button>
          </div>
        </div>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4">
        <div className="max-w-4xl mx-auto space-y-4">
          {messages.map((message) => (
            <div
              key={message.id}
              className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              <div className={`max-w-xs md:max-w-md lg:max-w-lg ${
                message.sender === 'user' 
                  ? 'order-2' 
                  : 'order-1'
              }`}>
                {message.sender === 'buddy' && (
                  <div className="flex items-center space-x-2 mb-1">
                    <BuddyAvatar mood={message.mood} />
                    <span className="text-sm font-medium">Buddy</span>
                  </div>
                )}
                <div className={`p-3 rounded-2xl ${
                  message.sender === 'user'
                    ? 'bg-gradient-to-r from-purple-500 to-pink-500 text-white ml-4'
                    : isDarkMode 
                      ? 'bg-gray-700 text-white mr-4'
                      : 'bg-white text-gray-800 mr-4 shadow-sm'
                } ${message.sender === 'buddy' ? 'font-medium' : ''}`}>
                  <p className="whitespace-pre-wrap break-words">{message.text}</p>
                </div>
              </div>
            </div>
          ))}

          {isTyping && (
            <div className="flex justify-start">
              <div className="flex items-center space-x-2 mb-1">
                <BuddyAvatar mood="happy" />
                <span className="text-sm font-medium">Buddy</span>
              </div>
              <div className={`p-3 rounded-2xl mr-4 ${
                isDarkMode ? 'bg-gray-700' : 'bg-white shadow-sm'
              }`}>
                <div className="flex space-x-1">
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                </div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* Input */}
      <div className={`p-4 border-t ${
        isDarkMode ? 'bg-gray-800 border-gray-700' : 'bg-white/70 backdrop-blur-sm border-purple-200'
      }`}>
        <div className="max-w-4xl mx-auto">
          <div className="flex items-center space-x-2 mb-2">
            <button
              onClick={getRandomPrompt}
              className={`px-3 py-1 rounded-full text-sm ${
                isDarkMode ? 'bg-gray-700 hover:bg-gray-600' : 'bg-purple-100 hover:bg-purple-200'
              } transition-colors flex items-center space-x-1`}
            >
              <Zap size={14} />
              <span>Feeling Lucky</span>
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <input
              ref={inputRef}
              type="text"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Type something... ðŸ˜„"
              className={`flex-1 p-3 rounded-full border-2 ${
                isDarkMode 
                  ? 'bg-gray-700 border-gray-600 text-white' 
                  : 'bg-white border-purple-200 text-gray-800'
              } focus:border-purple-500 focus:outline-none transition-colors`}
              disabled={isTyping}
            />
            <button
              onClick={handleSendMessage}
              disabled={isTyping || !inputMessage.trim()}
              className="p-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-full hover:from-purple-600 hover:to-pink-600 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-300 transform hover:scale-105"
            >
              <Send size={20} />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BuddyChat;
