import React, { useState, useEffect, useRef } from 'react';
import { Send, Sun, Moon, Zap, Heart, Smile, Laugh, User, MessageSquare, Settings, HelpCircle } from 'lucide-react';

const BuddyChat = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [showOnboarding, setShowOnboarding] = useState(true);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isConnected, setIsConnected] = useState(true);
  const [userProfile, setUserProfile] = useState({
    name: '',
    hobbies: '',
    favorites: ''
  });
  const [buddyMood, setBuddyMood] = useState('happy');
  const [showSidebar, setShowSidebar] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Enhanced mood states
  const moodStates = {
    happy: { emoji: '😊', color: '#3b82f6', animation: 'none' },
    excited: { emoji: '🤩', color: '#ec4899', animation: 'pulse' },
    confused: { emoji: '🤔', color: '#f59e0b', animation: 'bounce' },
    thinking: { emoji: '🤨', color: '#8b5cf6', animation: 'float' },
    sad: { emoji: '😢', color: '#64748b', animation: 'none' }
  };

  // Enhanced styles with better spacing and shadows
  const styles = {
    container: {
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      background: isDarkMode
        ? 'linear-gradient(135deg, #0f172a 0%, #1e293b 100%)'
        : 'linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)',
      color: isDarkMode ? '#f8fafc' : '#1e293b',
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif",
      transition: 'all 0.3s ease'
    },
    onboardingContainer: {
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px',
      background: isDarkMode
        ? 'linear-gradient(135deg, #0f172a 0%, #1e293b 100%)'
        : 'linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)'
    },
    onboardingCard: {
      maxWidth: '500px',
      width: '100%',
      padding: '40px',
      borderRadius: '24px',
      background: isDarkMode ? 'rgba(15, 23, 42, 0.95)' : 'rgba(255, 255, 255, 0.95)',
      boxShadow: isDarkMode
        ? '0 10px 30px rgba(0, 0, 0, 0.5)'
        : '0 10px 30px rgba(0, 0, 0, 0.1)',
      backdropFilter: 'blur(12px)',
      border: isDarkMode ? '1px solid rgba(255, 255, 255, 0.1)' : '1px solid rgba(0, 0, 0, 0.05)',
      color: isDarkMode ? '#f8fafc' : '#1e293b',
      transition: 'all 0.3s ease'
    },
    appContainer: {
      display: 'flex',
      flex: 1,
      overflow: 'hidden'
    },
    sidebar: {
      width: showSidebar ? '280px' : '70px',
      background: isDarkMode ? 'rgba(15, 23, 42, 0.9)' : 'rgba(255, 255, 255, 0.9)',
      backdropFilter: 'blur(10px)',
      borderRight: isDarkMode ? '1px solid rgba(255, 255, 255, 0.1)' : '1px solid rgba(0, 0, 0, 0.05)',
      padding: '20px 10px',
      display: 'flex',
      flexDirection: 'column',
      transition: 'width 0.3s ease',
      overflow: 'hidden'
    },
    sidebarCollapsed: {
      width: '70px',
      overflow: 'hidden'
    },
    sidebarItem: {
      display: 'flex',
      alignItems: 'center',
      padding: '12px 15px',
      margin: '5px 0',
      borderRadius: '12px',
      cursor: 'pointer',
      color: isDarkMode ? 'rgba(255, 255, 255, 0.8)' : 'rgba(0, 0, 0, 0.8)',
      transition: 'all 0.2s ease',
      ':hover': {
        background: isDarkMode ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.05)'
      }
    },
    sidebarItemExpanded: {
      justifyContent: 'flex-start',
      gap: '12px'
    },
    sidebarItemCollapsed: {
      justifyContent: 'center'
    },
    mainContent: {
      flex: 1,
      display: 'flex',
      flexDirection: 'column',
      overflow: 'hidden'
    },
    header: {
      padding: '15px 20px',
      background: isDarkMode ? 'rgba(15, 23, 42, 0.9)' : 'rgba(255, 255, 255, 0.9)',
      backdropFilter: 'blur(10px)',
      borderBottom: isDarkMode ? '1px solid rgba(255, 255, 255, 0.1)' : '1px solid rgba(0, 0, 0, 0.05)',
      position: 'sticky',
      top: 0,
      zIndex: 10,
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    headerContent: {
      display: 'flex',
      alignItems: 'center',
      gap: '15px',
      width: '100%'
    },
    avatar: {
      width: '50px',
      height: '50px',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '24px',
      transition: 'all 0.3s ease',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
    },
    messagesContainer: {
      flex: 1,
      overflowY: 'auto',
      padding: '20px',
      background: isDarkMode ? 'rgba(15, 23, 42, 0.5)' : 'rgba(239, 246, 255, 0.5)',
      display: 'flex',
      flexDirection: 'column'
    },
    messagesContent: {
      maxWidth: '800px',
      margin: '0 auto',
      width: '100%',
      display: 'flex',
      flexDirection: 'column'
    },
    messagesBubble: {
      maxWidth: '75%',
      marginBottom: '20px',
      padding: '14px 18px',
      borderRadius: '22px',
      wordWrap: 'break-word',
      boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
      position: 'relative',
      lineHeight: '1.5',
      fontSize: '15px'
    },
    inputContainer: {
      padding: '20px',
      background: isDarkMode ? 'rgba(15, 23, 42, 0.9)' : 'rgba(255, 255, 255, 0.9)',
      backdropFilter: 'blur(10px)',
      borderTop: isDarkMode ? '1px solid rgba(255, 255, 255, 0.1)' : '1px solid rgba(0, 0, 0, 0.05)'
    },
    input: {
      width: '100%',
      padding: '14px 20px',
      borderRadius: '30px',
      border: isDarkMode ? '1px solid rgba(255, 255, 255, 0.2)' : '1px solid rgba(0, 0, 0, 0.1)',
      background: isDarkMode ? 'rgba(30, 41, 59, 0.8)' : 'rgba(248, 250, 252, 0.9)',
      color: isDarkMode ? '#f8fafc' : '#1e293b',
      fontSize: '15px',
      outline: 'none',
      marginBottom: '10px',
      transition: 'all 0.2s ease',
      ':focus': {
        borderColor: '#3b82f6',
        boxShadow: '0 0 0 3px rgba(59, 130, 246, 0.2)'
      }
    },
    button: {
      padding: '12px 20px',
      borderRadius: '30px',
      border: 'none',
      background: 'linear-gradient(135deg, #3b82f6 0%, #6366f1 100%)',
      color: 'white',
      cursor: 'pointer',
      fontSize: '15px',
      fontWeight: '500',
      transition: 'all 0.2s ease',
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      ':hover': {
        opacity: 0.9,
        transform: 'translateY(-1px)'
      },
      ':active': {
        transform: 'translateY(0)'
      }
    },
    typingIndicator: {
      display: 'flex',
      gap: '5px',
      padding: '10px 14px',
      alignItems: 'center'
    },
    dot: {
      width: '8px',
      height: '8px',
      borderRadius: '50%',
      backgroundColor: '#9ca3af',
      animation: 'bounce 1.4s infinite ease-in-out'
    },
    statusIndicator: {
      display: 'flex',
      alignItems: 'center',
      gap: '6px',
      padding: '6px 12px',
      borderRadius: '20px',
      fontSize: '12px',
      fontWeight: '500',
      background: isConnected ? 'rgba(16, 185, 129, 0.2)' : 'rgba(239, 68, 68, 0.2)',
      color: isConnected ? '#10b981' : '#ef4444'
    },
    moodIndicator: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      padding: '8px 12px',
      borderRadius: '20px',
      background: isDarkMode ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.05)',
      fontSize: '13px',
      fontWeight: '500'
    }
  };

  // Load user data from localStorage on mount
  useEffect(() => {
    const savedProfile = localStorage.getItem('buddyUserProfile');
    if (savedProfile) {
      const profile = JSON.parse(savedProfile);
      setUserProfile(profile);
      setShowOnboarding(false);
      // Add welcome back message with more personality
      setTimeout(() => {
        const welcomeMessages = [
          `HEY THERE ${profile.name.toUpperCase()}! 👋 Long time no see! I was starting to miss you! What's been happening in your world?`,
          `WELCOME BACK ${profile.name.toUpperCase()}! 🎉 You came back! I knew you couldn't stay away from your favorite chat buddy for too long!`,
          `OH HAY ${profile.name.toUpperCase()}! 😊 It's so good to see you again! Ready to pick up where we left off?`,
          `HELLO AGAIN ${profile.name.toUpperCase()}! 🤗 I was just thinking about you! What's new and exciting in your life?`
        ];
        const randomMessage = welcomeMessages[Math.floor(Math.random() * welcomeMessages.length)];
        addBuddyMessage(randomMessage, 'excited');
      }, 500);
    }

    // Check for saved theme preference
    const savedTheme = localStorage.getItem('buddyThemePreference');
    if (savedTheme) {
      setIsDarkMode(savedTheme === 'dark');
    }
  }, []);

  // Save theme preference when it changes
  useEffect(() => {
    localStorage.setItem('buddyThemePreference', isDarkMode ? 'dark' : 'light');
  }, [isDarkMode]);

  // Auto-scroll to bottom with better behavior
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({
        behavior: 'smooth',
        block: 'nearest',
        inline: 'start'
      });
    }
  }, [messages, isTyping]);

  const addBuddyMessage = (text, mood = 'happy') => {
    setBuddyMood(mood);
    setMessages(prev => [...prev, {
      id: Date.now(),
      text,
      sender: 'buddy',
      timestamp: new Date(),
      mood
    }]);
  };

  const addUserMessage = (text) => {
    setMessages(prev => [...prev, {
      id: Date.now(),
      text,
      sender: 'user',
      timestamp: new Date()
    }]);
  };

  // Enhanced response system with more personality
  const getBuddyResponse = (userMessage) => {
    const lowerMessage = userMessage.toLowerCase();
    const responses = {
      greetings: [
        `Hey there ${userProfile.name}! 👋 What's up? How's your day going so far?`,
        `Hi ${userProfile.name}! 😊 So good to hear from you! What's on your mind today?`,
        `Hello ${userProfile.name}! 🌟 You're looking awesome today! What can I do for you?`,
        `Well hello there, ${userProfile.name}! 🤗 I was just thinking about you! What brings you here today?`
      ],
      confused: [
        "Hmm, I'm not sure I understand. Could you explain that a bit more? I really want to get what you're saying!",
        "I think I might have missed something there. Could you try saying that in a different way?",
        "I'm a little confused, but I want to understand! Could you give me more details?",
        "I'm not quite following. Maybe we could talk about something else? What do you like to do for fun?"
      ],
      excited: [
        "OMG that's so exciting! 🎉 Tell me more about that! I love hearing good news!",
        "Wow, that sounds amazing! 🤩 I'm so happy for you! What else is going great in your life?",
        "That's incredible! You're making my day better just by sharing that! 💖 What other awesome things have been happening?",
        "No way! That's so cool! 😍 I wish I could high-five you right now! What's the story behind that?"
      ],
      hobbies: [
        `You know, I was just thinking about ${userProfile.hobbies}. What do you love most about it?`,
        `I've been meaning to ask - how did you get into ${userProfile.hobbies}? It sounds so interesting!`,
        `Tell me more about ${userProfile.hobbies}! What's the most fun part about it for you?`,
        `I bet you're really good at ${userProfile.hobbies}! What's your favorite memory related to that?`
      ],
      favorites: [
        `Oh, I remember you love ${userProfile.favorites}! What's your favorite thing about that?`,
        `You've mentioned ${userProfile.favorites} before. What's something new you've discovered about it lately?`,
        `Tell me more about why you love ${userProfile.favorites}! I'd love to understand what makes it special to you.`,
        `If you could only keep one thing about ${userProfile.favorites}, what would it be?`
      ],
      default: [
        "That's interesting! What else would you like to talk about?",
        "I see. How does that make you feel?",
        "Tell me more about that! I'm really interested in what you have to say.",
        "That's cool! What other things have been on your mind lately?",
        "I appreciate you sharing that with me. Is there anything else you'd like to discuss?"
      ],
      questions: [
        "That's a great question! Let me think about that for a moment...",
        "Hmm, interesting question! What do you think about it?",
        "I love that you're asking questions! It shows you're really engaged. What are your thoughts?",
        "You know, I'm not sure I have the perfect answer, but I'd love to explore that with you! What are your ideas?"
      ]
    };

    // More sophisticated response selection
    if (lowerMessage.includes('hello') || lowerMessage.includes('hi') || lowerMessage.includes('hey') || lowerMessage.includes('greetings')) {
      return responses.greetings[Math.floor(Math.random() * responses.greetings.length)];
    } else if (lowerMessage.includes('what') || lowerMessage.includes('how') || lowerMessage.includes('why') || lowerMessage.includes('?')) {
      return responses.questions[Math.floor(Math.random() * responses.questions.length)];
    } else if (lowerMessage.includes('awesome') || lowerMessage.includes('cool') || lowerMessage.includes('amazing') || lowerMessage.includes('great') || lowerMessage.includes('exciting')) {
      return responses.excited[Math.floor(Math.random() * responses.excited.length)];
    } else if (lowerMessage.includes('hobby') || lowerMessage.includes('interest') || lowerMessage.includes('like') || lowerMessage.includes('love')) {
      return responses.hobbies[Math.floor(Math.random() * responses.hobbies.length)];
    } else if (lowerMessage.includes('favorite') || lowerMessage.includes('best') || lowerMessage.includes('top') || lowerMessage.includes('favourites')) {
      return responses.favorites[Math.floor(Math.random() * responses.favorites.length)];
    } else {
      return responses.default[Math.floor(Math.random() * responses.default.length)];
    }
  };

  const simulateTyping = () => {
    setIsTyping(true);
    // Random typing duration between 0.8 and 2.5 seconds
    const typingDuration = 800 + Math.random() * 1700;
    setTimeout(() => {
      setIsTyping(false);
    }, typingDuration);
  };

  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    const userMsg = inputMessage;
    setInputMessage('');
    addUserMessage(userMsg);

    // Simulate API call with more realistic timing
    simulateTyping();

    try {
      // Simulate network delay with random variation
      const delay = 500 + Math.random() * 1000;
      await new Promise(resolve => setTimeout(resolve, delay));

      if (!isConnected) {
        const offlineMessages = [
          "Oh no! It seems like we're having some connection issues. 😕 Let's try that again in a moment?",
          "Uh oh, I think my brain just went offline for a second! 🤯 Could you try sending that again?",
          "Oops! Looks like we got disconnected. 😢 Don't worry, I'm sure we'll reconnect soon!",
          "Hmm, something's not right with our connection. 🤔 Let's take a breath and try again in a bit!"
        ];
        addBuddyMessage(offlineMessages[Math.floor(Math.random() * offlineMessages.length)], 'confused');
        return;
      }

      const response = getBuddyResponse(userMsg);
      // Sometimes add a follow-up question to keep conversation flowing
      const shouldAddFollowUp = Math.random() > 0.6;
      if (shouldAddFollowUp) {
        const followUps = [
          "What else is on your mind today?",
          "That's really interesting! What other thoughts do you have about that?",
          "I love this conversation! What would you like to talk about next?",
          "This has been great! Is there anything else you'd like to share?",
          "You're amazing to talk to! What other topics would you like to explore?"
        ];
        setTimeout(() => {
          addBuddyMessage(followUps[Math.floor(Math.random() * followUps.length)]);
        }, 1000);
      }
      addBuddyMessage(response);

    } catch (error) {
      const errorMessages = [
        "Oh no! Something went wrong on my end. 😳 Could you try that again?",
        "I think I just had a little brain freeze! ❄️ Let's try that again!",
        "Oops! I must have dropped that thought. 🤕 Could you repeat what you said?",
        "Uh oh, I think I need a quick reboot! 🔄 Let's try that conversation again!"
      ];
      addBuddyMessage(errorMessages[Math.floor(Math.random() * errorMessages.length)], 'confused');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleOnboardingSubmit = () => {
    if (!userProfile.name.trim()) return;

    localStorage.setItem('buddyUserProfile', JSON.stringify(userProfile));
    setShowOnboarding(false);

    // More personalized welcome message
    setTimeout(() => {
      const welcomeMessages = [
        `HEY THERE ${userProfile.name.toUpperCase()}! 👋 Welcome to our little chat space! I'm Buddy, your new virtual friend! I'm really excited to get to know you better!`,
        `HI ${userProfile.name.toUpperCase()}! 😊 So nice to meet you! I'm Buddy, and I'm here to chat about anything and everything with you!`,
        `HELLO ${userProfile.name.toUpperCase()}! 🌟 Welcome to our conversation corner! I'm Buddy, and I can't wait to hear all about you!`,
        `WELCOME ${userProfile.name.toUpperCase()}! 🤗 I'm Buddy, your new chat companion! Let's have some amazing conversations together!`
      ];

      const randomMessage = welcomeMessages[Math.floor(Math.random() * welcomeMessages.length)];

      let fullMessage = randomMessage;

      if (userProfile.hobbies) {
        const hobbyComments = [
          `I see you're into ${userProfile.hobbies} - that's so cool! I'd love to hear more about that!`,
          `Your interest in ${userProfile.hobbies} sounds fascinating! Tell me all about it!`,
          `Wow, ${userProfile.hobbies} is awesome! What do you love most about it?`,
          `I've heard great things about ${userProfile.hobbies}! How did you get into that?`
        ];
        fullMessage += ` ${hobbyComments[Math.floor(Math.random() * hobbyComments.length)]}`;
      }

      if (userProfile.favorites) {
        const favoriteComments = [
          `And your love for ${userProfile.favorites} is amazing! What's your favorite thing about that?`,
          `Oh, ${userProfile.favorites} is so interesting! What draws you to that?`,
          `I'd love to know more about why you like ${userProfile.favorites} so much!`,
          `Your passion for ${userProfile.favorites} is inspiring! Tell me more about that!`
        ];
        fullMessage += ` ${favoriteComments[Math.floor(Math.random() * favoriteComments.length)]}`;
      }

      fullMessage += " So, what would you like to talk about first? I'm all ears!";

      addBuddyMessage(fullMessage, 'excited');
    }, 500);
  };

  const getRandomPrompt = () => {
    const prompts = [
      "What's something that made you smile recently?",
      "If you could travel anywhere right now, where would you go?",
      "What's your favorite way to spend a lazy Sunday?",
      "What's something you're really proud of?",
      "What's the best meal you've had recently?",
      "What's a book or movie that really impacted you?",
      "What's something you're looking forward to?",
      "What's a hobby you've always wanted to try?",
      "What's your favorite season and why?",
      "What's something that always cheers you up?"
    ];
    setInputMessage(prompts[Math.floor(Math.random() * prompts.length)]);
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  const toggleSidebar = () => {
    setShowSidebar(!showSidebar);
  };

  const BuddyAvatar = ({ mood }) => {
    const currentMood = moodStates[mood] || moodStates.happy;
    const avatarStyle = {
      ...styles.avatar,
      background: currentMood.color,
      animation: currentMood.animation === 'none' ? 'none' : `${currentMood.animation} 2s infinite ease-in-out`,
      fontSize: '28px'
    };

    return (
      <div style={avatarStyle}>
        {currentMood.emoji}
      </div>
    );
  };

  const MessageBubble = ({ message }) => {
    const isUser = message.sender === 'user';
    const currentMood = moodStates[message.mood] || moodStates.happy;

    return (
      <div style={{
        display: 'flex',
        justifyContent: isUser ? 'flex-end' : 'flex-start',
        marginBottom: '15px',
        opacity: 0,
        animation: 'fadeIn 0.3s ease-in forwards',
        animationDelay: `${messages.indexOf(message) * 0.1}s`
      }}>
        <div style={{ maxWidth: '80%' }}>
          {!isUser && (
            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '8px',
              marginBottom: '5px',
              marginLeft: '45px'
            }}>
              <BuddyAvatar mood={message.mood} />
              <div style={{
                display: 'flex',
                flexDirection: 'column'
              }}>
                <span style={{
                  fontSize: '14px',
                  fontWeight: '600',
                  color: isDarkMode ? '#f8fafc' : '#1e293b'
                }}>
                  Buddy
                </span>
                <div style={styles.moodIndicator}>
                  <span style={{
                    width: '10px',
                    height: '10px',
                    borderRadius: '50%',
                    backgroundColor: currentMood.color
                  }}></span>
                  <span>{currentMood.emoji}</span>
                </div>
              </div>
            </div>
          )}
          <div style={{
            ...styles.messagesBubble,
            background: isUser
              ? 'linear-gradient(135deg, #3b82f6 0%, #6366f1 100%)'
              : isDarkMode
                ? 'rgba(30, 41, 59, 0.8)'
                : 'rgba(255, 255, 255, 0.9)',
            color: isUser ? 'white' : (isDarkMode ? '#f8fafc' : '#1e293b'),
            marginLeft: isUser ? '45px' : '0',
            marginRight: isUser ? '0' : '45px',
            borderBottomLeftRadius: isUser ? '22px' : '4px',
            borderBottomRightRadius: isUser ? '4px' : '22px'
          }}>
            <p style={{
              margin: 0,
              whiteSpace: 'pre-wrap',
              wordBreak: 'break-word',
              fontWeight: isUser ? '500' : '400'
            }}>
              {message.text}
            </p>
            <div style={{
              display: 'flex',
              justifyContent: 'flex-end',
              marginTop: '5px',
              fontSize: '11px',
              opacity: 0.7
            }}>
              {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
            </div>
          </div>
        </div>
      </div>
    );
  };

  if (showOnboarding) {
    return (
      <div style={styles.onboardingContainer}>
        <div style={styles.onboardingCard}>
          <div style={{ textAlign: 'center', marginBottom: '30px' }}>
            <div style={{
              width: '100px',
              height: '100px',
              borderRadius: '50%',
              background: 'linear-gradient(135deg, #3b82f6 0%, #6366f1 100%)',
              margin: '0 auto 20px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              boxShadow: '0 8px 24px rgba(0, 0, 0, 0.1)'
            }}>
              <Smile size={48} color="white" />
            </div>
            <h1 style={{
              fontSize: '32px',
              fontWeight: '800',
              marginBottom: '10px',
              background: 'linear-gradient(135deg, #3b82f6 0%, #6366f1 100%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent'
            }}>
              Welcome to Buddy!
            </h1>
            <p style={{
              fontSize: '17px',
              opacity: 0.8,
              lineHeight: '1.6',
              maxWidth: '400px',
              margin: '0 auto 20px'
            }}>
              I'm your new virtual friend, here to chat about anything and everything with you!
            </p>
          </div>

          <div style={{
            display: 'flex',
            flexDirection: 'column',
            gap: '20px'
          }}>
            <div>
              <label style={{
                display: 'block',
                fontSize: '15px',
                fontWeight: '600',
                marginBottom: '8px',
                color: isDarkMode ? '#f8fafc' : '#1e293b'
              }}>
                What should I call you? *
              </label>
              <input
                type="text"
                value={userProfile.name}
                onChange={(e) => setUserProfile({...userProfile, name: e.target.value})}
                style={{
                  ...styles.input,
                  marginBottom: 0,
                  padding: '14px 20px',
                  fontSize: '15px'
                }}
                placeholder="Your name or nickname"
                required
              />
            </div>

            <div>
              <label style={{
                display: 'block',
                fontSize: '15px',
                fontWeight: '600',
                marginBottom: '8px',
                color: isDarkMode ? '#f8fafc' : '#1e293b'
              }}>
                What are you into?
              </label>
              <input
                type="text"
                value={userProfile.hobbies}
                onChange={(e) => setUserProfile({...userProfile, hobbies: e.target.value})}
                style={{
                  ...styles.input,
                  marginBottom: 0,
                  padding: '14px 20px'
                }}
                placeholder="Gaming, music, sports, cooking, reading..."
              />
            </div>

            <div>
              <label style={{
                display: 'block',
                fontSize: '15px',
                fontWeight: '600',
                marginBottom: '8px',
                color: isDarkMode ? '#f8fafc' : '#1e293b'
              }}>
                Favorite movies, songs, or random facts?
              </label>
              <input
                type="text"
                value={userProfile.favorites}
                onChange={(e) => setUserProfile({...userProfile, favorites: e.target.value})}
                style={{
                  ...styles.input,
                  marginBottom: 0,
                  padding: '14px 20px'
                }}
                placeholder="Marvel movies, Taylor Swift, cats are liquid, space facts..."
              />
            </div>

            <button
              onClick={handleOnboardingSubmit}
              style={{
                ...styles.button,
                width: '100%',
                justifyContent: 'center',
                fontSize: '17px',
                fontWeight: '600',
                marginTop: '10px',
                padding: '16px 24px',
                boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
              }}
            >
              <Heart size={18} style={{ marginRight: '8px' }} />
              Let's be friends!
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      {/* CSS Animations */}
      <style>
        {`
          @keyframes bounce {
            0%, 20%, 53%, 80%, 100% { transform: translateY(0); }
            40%, 43% { transform: translateY(-8px); }
            70% { transform: translateY(-4px); }
            90% { transform: translateY(-2px); }
          }
          @keyframes pulse {
            0% { transform: scale(1); }
            50% { transform: scale(1.05); }
            100% { transform: scale(1); }
          }
          @keyframes float {
            0% { transform: translateY(0px); }
            50% { transform: translateY(-5px); }
            100% { transform: translateY(0px); }
          }
          @keyframes fadeIn {
            from { opacity: 0; transform: translateY(10px); }
            to { opacity: 1; transform: translateY(0); }
          }
          .bounce-dot:nth-child(1) { animation-delay: -0.32s; }
          .bounce-dot:nth-child(2) { animation-delay: -0.16s; }
          .bounce-dot:nth-child(3) { animation-delay: 0s; }
        `}
      </style>

      <div style={styles.appContainer}>
        {/* Sidebar */}
        <div style={{
          ...styles.sidebar,
          ...(showSidebar ? {} : styles.sidebarCollapsed)
        }}>
          <div style={{
            display: 'flex',
            flexDirection: 'column',
            height: '100%',
            overflow: 'hidden'
          }}>
            <div style={{
              display: 'flex',
              justifyContent: showSidebar ? 'space-between' : 'center',
              alignItems: 'center',
              marginBottom: '30px',
              padding: '0 10px'
            }}>
              {showSidebar && (
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '10px'
                }}>
                  <BuddyAvatar mood={buddyMood} />
                  <h2 style={{
                    fontSize: '18px',
                    fontWeight: '700',
                    margin: 0,
                    color: isDarkMode ? '#f8fafc' : '#1e293b'
                  }}>
                    Buddy
                  </h2>
                </div>
              )}
              <button
                onClick={toggleSidebar}
                style={{
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  color: isDarkMode ? 'rgba(255, 255, 255, 0.8)' : 'rgba(0, 0, 0, 0.8)',
                  padding: '8px',
                  borderRadius: '8px',
                  ':hover': {
                    background: isDarkMode ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.05)'
                  }
                }}
              >
                {showSidebar ? '←' : '→'}
              </button>
            </div>

            <div style={{
              display: 'flex',
              flexDirection: 'column',
              gap: '8px',
              flex: 1,
              overflowY: 'auto',
              padding: '0 10px'
            }}>
              <div style={{
                ...styles.sidebarItem,
                ...(showSidebar ? styles.sidebarItemExpanded : styles.sidebarItemCollapsed)
              }}>
                <MessageSquare size={18} />
                {showSidebar && <span>Chats</span>}
              </div>

              <div style={{
                ...styles.sidebarItem,
                ...(showSidebar ? styles.sidebarItemExpanded : styles.sidebarItemCollapsed)
              }}>
                <User size={18} />
                {showSidebar && <span>Profile</span>}
              </div>

              <div style={{
                ...styles.sidebarItem,
                ...(showSidebar ? styles.sidebarItemExpanded : styles.sidebarItemCollapsed)
              }}>
                <Settings size={18} />
                {showSidebar && <span>Settings</span>}
              </div>
            </div>

            <div style={{
              padding: '0 10px',
              marginTop: 'auto'
            }}>
              <div style={{
                ...styles.sidebarItem,
                ...(showSidebar ? styles.sidebarItemExpanded : styles.sidebarItemCollapsed),
                marginTop: '20px'
              }}>
                <HelpCircle size={18} />
                {showSidebar && <span>Help</span>}
              </div>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div style={styles.mainContent}>
          {/* Header */}
          <div style={styles.header}>
            <div style={styles.headerContent}>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '12px'
              }}>
                <BuddyAvatar mood={buddyMood} />
                <div>
                  <h1 style={{
                    fontSize: '20px',
                    fontWeight: '700',
                    margin: 0,
                    color: isDarkMode ? '#f8fafc' : '#1e293b'
                  }}>
                    Buddy
                  </h1>
                  <p style={{
                    fontSize: '13px',
                    opacity: 0.7,
                    margin: 0,
                    color: isDarkMode ? 'rgba(255, 255, 255, 0.7)' : 'rgba(0, 0, 0, 0.7)'
                  }}>
                    Your virtual friend
                  </p>
                </div>
              </div>

              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '15px'
              }}>
                <div style={styles.statusIndicator}>
                  <div style={{
                    width: '8px',
                    height: '8px',
                    borderRadius: '50%',
                    backgroundColor: isConnected ? '#10b981' : '#ef4444',
                    marginRight: '4px'
                  }}></div>
                  {isConnected ? 'Online' : 'Offline'}
                </div>

                <button
                  onClick={toggleTheme}
                  style={{
                    ...styles.button,
                    padding: '8px',
                    borderRadius: '50%',
                    background: isDarkMode ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)',
                    width: '40px',
                    height: '40px',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center'
                  }}
                  aria-label={isDarkMode ? 'Switch to light mode' : 'Switch to dark mode'}
                >
                  {isDarkMode ? <Sun size={18} /> : <Moon size={18} />}
                </button>
              </div>
            </div>
          </div>

          {/* Messages */}
          <div style={styles.messagesContainer}>
            <div style={styles.messagesContent}>
              {messages.length === 0 && !isTyping && (
                <div style={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                  height: '100%',
                  textAlign: 'center',
                  padding: '40px',
                  opacity: 0.7
                }}>
                  <div style={{
                    width: '120px',
                    height: '120px',
                    borderRadius: '50%',
                    background: 'linear-gradient(135deg, #3b82f6 0%, #6366f1 100%)',
                    marginBottom: '20px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    boxShadow: '0 8px 24px rgba(0, 0, 0, 0.1)'
                  }}>
                    <MessageSquare size={48} color="white" />
                  </div>
                  <h2 style={{
                    fontSize: '22px',
                    fontWeight: '700',
                    marginBottom: '10px',
                    color: isDarkMode ? '#f8fafc' : '#1e293b'
                  }}>
                    Welcome to Buddy Chat!
                  </h2>
                  <p style={{
                    fontSize: '15px',
                    maxWidth: '400px',
                    marginBottom: '30px',
                    color: isDarkMode ? 'rgba(255, 255, 255, 0.8)' : 'rgba(0, 0, 0, 0.8)'
                  }}>
                    Start a conversation with your virtual friend. Ask questions, share thoughts, or just chat about your day!
                  </p>
                  <button
                    onClick={getRandomPrompt}
                    style={{
                      ...styles.button,
                      padding: '12px 24px',
                      fontSize: '15px',
                      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
                    }}
                  >
                    <Zap size={16} style={{ marginRight: '8px' }} />
                    Suggest a topic
                  </button>
                </div>
              )}

              {messages.map((message) => (
                <MessageBubble key={message.id} message={message} />
              ))}

              {isTyping && (
                <div style={{
                  display: 'flex',
                  justifyContent: 'flex-start',
                  marginBottom: '15px',
                  opacity: 0,
                  animation: 'fadeIn 0.3s ease-in forwards'
                }}>
                  <div style={{ maxWidth: '75%' }}>
                    <div style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '8px',
                      marginBottom: '5px',
                      marginLeft: '45px'
                    }}>
                      <BuddyAvatar mood="thinking" />
                      <div style={{
                        display: 'flex',
                        flexDirection: 'column'
                      }}>
                        <span style={{
                          fontSize: '14px',
                          fontWeight: '600',
                          color: isDarkMode ? '#f8fafc' : '#1e293b'
                        }}>
                          Buddy
                        </span>
                        <div style={styles.moodIndicator}>
                          <span style={{
                            width: '10px',
                            height: '10px',
                            borderRadius: '50%',
                            backgroundColor: moodStates.thinking.color
                          }}></span>
                          <span>{moodStates.thinking.emoji}</span>
                        </div>
                      </div>
                    </div>
                    <div style={{
                      ...styles.messagesBubble,
                      background: isDarkMode ? 'rgba(30, 41, 59, 0.8)' : 'rgba(255, 255, 255, 0.9)',
                      marginRight: '20px',
                      marginLeft: '45px',
                      borderBottomRightRadius: '22px',
                      borderBottomLeftRadius: '4px'
                    }}>
                      <div style={styles.typingIndicator}>
                        <div className="bounce-dot" style={{
                          ...styles.dot,
                          backgroundColor: isDarkMode ? '#9ca3af' : '#6b7280',
                          animationName: 'bounce',
                          animationDuration: '1.4s',
                          animationIterationCount: 'infinite',
                          animationFillMode: 'both'
                        }}></div>
                        <div className="bounce-dot" style={{
                          ...styles.dot,
                          backgroundColor: isDarkMode ? '#9ca3af' : '#6b7280',
                          animationName: 'bounce',
                          animationDuration: '1.4s',
                          animationIterationCount: 'infinite',
                          animationFillMode: 'both',
                          animationDelay: '0.16s'
                        }}></div>
                        <div className="bounce-dot" style={{
                          ...styles.dot,
                          backgroundColor: isDarkMode ? '#9ca3af' : '#6b7280',
                          animationName: 'bounce',
                          animationDuration: '1.4s',
                          animationIterationCount: 'infinite',
                          animationFillMode: 'both',
                          animationDelay: '0.32s'
                        }}></div>
                      </div>
                    </div>
                  </div>
                </div>
              )}
              <div ref={messagesEndRef} />
            </div>
          </div>

          {/* Input */}
          <div style={styles.inputContainer}>
            <div style={{
              maxWidth: '800px',
              margin: '0 auto',
              width: '100%'
            }}>
              <div style={{
                display: 'flex',
                gap: '10px',
                marginBottom: '15px',
                justifyContent: 'space-between',
                alignItems: 'center'
              }}>
                <button
                  onClick={getRandomPrompt}
                  style={{
                    ...styles.button,
                    background: isDarkMode ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.05)',
                    color: isDarkMode ? '#f8fafc' : '#1e293b',
                    fontSize: '14px',
                    padding: '10px 16px',
                    display: 'flex',
                    alignItems: 'center'
                  }}
                >
                  <Zap size={16} style={{ marginRight: '6px' }} />
                  Suggest a topic
                </button>

                <div style={styles.statusIndicator}>
                  <div style={{
                    width: '8px',
                    height: '8px',
                    borderRadius: '50%',
                    backgroundColor: isConnected ? '#10b981' : '#ef4444',
                    marginRight: '4px'
                  }}></div>
                  {isConnected ? 'Connected' : 'Disconnected'}
                </div>
              </div>

              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '10px'
              }}>
                <input
                  ref={inputRef}
                  type="text"
                  value={inputMessage}
                  onChange={(e) => setInputMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Type your message here..."
                  style={{
                    ...styles.input,
                    flex: 1,
                    marginBottom: 0,
                    padding: '14px 20px',
                    fontSize: '15px'
                  }}
                  disabled={isTyping}
                />
                <button
                  onClick={handleSendMessage}
                  disabled={isTyping || !inputMessage.trim()}
                  style={{
                    ...styles.button,
                    padding: '14px',
                    borderRadius: '50%',
                    width: '50px',
                    height: '50px',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    opacity: (isTyping || !inputMessage.trim()) ? 0.5 : 1,
                    cursor: (isTyping || !inputMessage.trim()) ? 'not-allowed' : 'pointer'
                  }}
                  aria-label="Send message"
                >
                  <Send size={18} />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BuddyChat;
